import {GET_ALL_POKEMONS,GET_DETAIL_POKEMONS,CLEAN_POKEMONS_DETAILS,GET_TYPES,GET_DB_POKEMONS} from "./actions-types";
import axios from "axios";


export const getAllPokemos = () => {
    return function(dispatch) {
        fetch("http://localhost:3001/pokemons")
        .then(result => result.json())
        .then(data => {
            console.log(holas)
            return dispatch({type: GET_ALL_POKEMONS, payload: data}); // lo despachas --> con un type
        })
    }
}
