import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { getAllTypes } from "../../Redux/actions";
import TitleBar from "../TitleBar/TitleBar";
import Footer from "../Footer/Footer";
import styles from "./Create.module.css";


const Create =()=>{
    //traemos el dispacth para usarlo
    const dispatch = useDispatch();
    //se me traigo a todos los tipos de pokemones
    const tipospokemones = useSelector(state => state.types)// Aca esta incluido el "All",para los filtros
    const newtipospokemones = tipospokemones.filter(a=>a !== "All")// Por ende le quitamos dicha propiedad, por que aca no nos es de ayuda


    console.log("tipospokemones");
    // const [imageSrc, setImageSrc] = useState(""); // estado loval Img
    const [selectedName, setSelectedName] = useState(""); // estado loval Img
    const [selecteHp, setSelectedHp] = useState(""); // estado local de Vida
    const [selecteAttack, setSelectedAttack] = useState(""); // estado local de Ataque
    const [selecteDefense, setSelectedSpeed] = useState(""); // estado local de Speed
    const [selectedTypesArray ,setSelectedTypesArray ] = useState("")//Estado local Array types
    console.log(selectedTypesArray);

    //Ahora modificaremos el estado local de Types
    const arrayTypesFinal = selectedTypesArray =""? [] : [...selectedTypesArray,selectedTypesArray];


    //Hacemos un manejador
    const handleArrayTypesChange = (e)=>{
        setSelectedTypesArray(e.target.value)
    }

    //Cuando se comienze la pagina, se trae todos los tipos de pokemones

    useEffect(()=>{
        dispatch(getAllTypes());
   
    },[]);
    

    // function previewImage() {
    //     var file = document.querySelector("#imagen").files[0];
    //     var reader = new FileReader();
  
    //     reader.addEventListener("load", function () {
    //         setImageSrc(reader.result);
    //     }, false);
  
    //     if (file) {
    //         reader.readAsDataURL(file);
    //     }
    // }

    return(
        <div className={styles.cont}>
            <TitleBar type={"/create"} text={"Bienvenidos, Toco Crear un pokemon"}></TitleBar>
            <form action="">

            <div className={styles.card}>
                <form className={styles.form}>

                    <label  className={styles.labe} htmlFor="">Name:<input placeholder="Name" type="text" value=""/></label>
                    
                    <label className={styles.labe} htmlFor="">Hp:<input placeholder="Pokemon life" type="text" value=""/></label>
                    
                    <label className={styles.labe} htmlFor="">Attack:<input placeholder="Pokemon Attack" type="text" value=""/></label>
                    
                    <label className={styles.labe} htmlFor="">Defense:<input placeholder="Pokemon Defense" type="text" value=""/></label>
                    
                    <label className={styles.labe} htmlFor="">Speed:<input placeholder="Pokemon Speed" type="text" value=""/></label>
                    {/*  Entramos a los tipos de pokemones, una lista desplegable /}
                    <label className={styles.labe} htmlFor="">Typos de pokemon: 
                    
                    <select value='' onChange={handleArrayTypesChange}>
                            {newtipospokemones.map(tipo =>(
                        <option value={tipo} >{tipo}</option>))
                            }
                    </select>
                            
                    </label>
                    


                    {/* <input className={styles.labe} type="file" id="imagen" name="imagen" onChange={previewImage} />
                    <div className={styles.labe}>
                    <img className={styles.imgg} id="preview" src={imageSrc} alt="Vista previa"></img>
                    </div>
                    <input  type="submit" value="Subir imagen"></input> */}
                </form>    
            </div>
            </form>
            <Footer></Footer>
        </div>
    )
}

export default Create;
