import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { getAllTypes, postPokeArray } from "../../Redux/actions";
import TitleBar from "../TitleBar/TitleBar";
import Footer from "../Footer/Footer";
import styles from "./Create.module.css";


const Create =()=>{
    //traemos el dispacth para usarlo
    const dispatch = useDispatch();
    //se me traigo a todos los tipos de pokemones
    const tipospokemones = useSelector(state => state.types)// Aca esta incluido el "All",para los filtros
    const newtipospokemones = tipospokemones.filter(a=>a !== "All")// Por ende le quitamos dicha propiedad, por que aca no nos es de ayuda
    
    
  
    // const [imageSrc, setImageSrc] = useState(""); // estado loval Img
    const [selectedName, setSelectedName] = useState(""); // estado loval Img
    const [selectedHp, setSelectedHp] = useState(""); // estado local de Vida
    const [selectedAttack, setSelectedAttack] = useState(""); // estado local de Ataque
    const [selectedDefense, setSelectedDefense] = useState("");// estado local de Defense
    const [selectedImg, setSelectedImg] = useState("");// estado local de Defense
    const [selectedSpeed, setSelectedSpeed] = useState(""); // estado local de Speed
    const [selectedTypesArray ,setSelectedTypesArray ] = useState([]);//Estado local Array types
    const [selectedErros, setSelectedErros] = useState([]);

    
    
   
    const arrayPost = {
        "name": selectedName,
        "img": selectedImg,
        "hp": selectedHp,
        "attack": selectedAttack,
        "defense": selectedDefense,
        "speed": selectedSpeed,
        "types": selectedTypesArray.map(a=>tipospokemones.indexOf(a))
    }

    console.log(arrayPost);
    console.log();
    //Ahora modificaremos el estado local de Types
    // const arrayTypesFinal = selectedTypesArray =""? [] : [...selectedTypesArray,selectedTypesArray];


    //Hacemos un manejador
    //Manejador de Selecionar Nombre
    const handleNameChange = (e)=>{
        setSelectedName(e.target.value)
    }
    const handleHpChange =(e)=>{
        setSelectedHp(e.target.value)
    }
    const handleAttackChange =(e)=>{
        setSelectedAttack(e.target.value)
    }
    const handleDefenseChange =(e)=>{
        setSelectedDefense(e.target.value)
    }
    const handleSpeedChange =(e)=>{
        setSelectedSpeed(e.target.value)
    }
    //Manejador de tipos agregados 
    const handleArrayTypesChange = (e)=>{
        setSelectedTypesArray([...selectedTypesArray,e.target.value])
    }
    //Manejador de imagen 
    const handleLinkImgChange = (e)=>{
        setSelectedImg(e.target.value)
    }

    //manejador de limpiar array
    const handleArrayLimpiar = (e)=>{
        setSelectedTypesArray([])
    }

    //? ------------------------- enviar al servidor
    const handleInputPostSumit = (event)=>{
        event.preventDefault();
       if(selectedErros.length === 0 ){

           postPokeArray(arrayPost)
   
           document.getElementById("Name").value = "";
           document.getElementById("Hp").value = "";
           document.getElementById("Attack").value = "";
           document.getElementById("Defense").value = "";
           document.getElementById("Speed").value = "";
           document.getElementById("Link").value = "";
           
       }else{
        
        
       }
    }
    
    const erros = [];
    if(document.getElementById("Name")>15 ) erros.push({name:"El nombe no debe exceder de 15 caracteres"})





    //Cuando se comienze la pagina, se trae todos los tipos de pokemones

    useEffect(()=>{
        dispatch(getAllTypes());
        
    },[]);
    

  

    return(
        <div className={styles.cont}>
            <TitleBar type={"/create"} text={"Bienvenidos, Toco Crear un pokemon"}></TitleBar>
           

            <div className={styles.card}>
                <form className={styles.form} onSubmit={handleInputPostSumit}>

                    <label className={styles.labe} htmlFor="">Name:<input id="Name" onChange={handleNameChange } placeholder="Name" type="text" /></label>
                    <a>{erros.name}</a>
                    <label className={styles.labe} htmlFor="">Hp:<input id="Hp" onChange={handleHpChange} placeholder="Pokemon life" type="text" /></label>
                    
                    <label className={styles.labe} htmlFor="">Attack:<input id="Attack" onChange={handleAttackChange} placeholder="Pokemon Attack" type="text"  /></label>
                    
                    <label className={styles.labe} htmlFor="">Defense:<input id="Defense" onChange={handleDefenseChange} placeholder="Pokemon Defense" type="text" /></label>
                    
                    <label className={styles.labe} htmlFor="">Speed:<input id="Speed" onChange={handleSpeedChange} placeholder="Pokemon Speed" type="text" /></label>
                 
                    <label className={styles.labe} htmlFor="">Typos de pokemon: 
                    
                    <select value='' onChange={handleArrayTypesChange}>
                            {tipospokemones.map(tipo =>(
                        <option value={tipo} >{tipo}</option>))
                            }
                    </select>
                    <br />
                    <label htmlFor="">`Typos seleccionados:{selectedTypesArray.join(" -")}`</label>
                    <br />
                    <button type="button" onClick={handleArrayLimpiar}>Limpiar tipos seleccionados</button>
                    </label>
                    
                    <label className={styles.labe} htmlFor="">Image Link:<input id="Link" onChange={handleLinkImgChange} placeholder="Pokemon Speed" type="text" /></label>
                    <br />
                    <button id="enviar" type="submit" onClick={handleInputPostSumit} >Submit</button>
              
                </form>    
            </div>

           
            <Footer></Footer>
        </div>
    )
}

export default Create;
