import { Router } from "react-router-dom"
import { useSelector, useDispatch } from "react-redux"; 
import { useEffect, useState } from "react";
import { getAllPokemos, getAllTypes } from "../../Redux/actions";
import styles from "./Ahome.module.css";
import TitleBar from "../TitleBar/TitleBar";
import Footer from "../Footer/Footer";
import pika2 from "../../Assets/fondototal.jpg";
import Cards from "../Cards/Cards";
import Search from "../Search/Search";

const Ahome= ()=>{
    const dispatch = useDispatch();
    const pokemons = useSelector(state => state.pokemones)
    const tipospokemones = useSelector(state => state.types)
    const [selectedType, setSelectedType] = useState("All");
    const [selectedFuente, setSelectedFuente] = useState("All");
    console.log(pokemons);

    //-------Filtro de pokemones de BD con Types:-------
    console.log("comienzo pokenmons");
    console.log(pokemons);
    console.log("comienzo");
    const newPokemonArray = pokemons.map(p => {
        return {
            attack: pokemons.attack,
            // defense: pokemons.defense,
            // hp: parseInt(p.hp.split(" ")[0]),
            // id: p.id,
            // img: p.img,
            // name: p.name,
            // speed: p.speed,
            // types: p.Types?.map(type => type,toLowerCase()) || []
        };
    });
    
    console.log(newPokemonArray);
    console.log("fin");
    
    const source = useSelector(state => state.selectFiltro)

    

    //Filtra la lista de pokemones                            aca es donde filtra
    const filteredPokemon = selectedType === "All" ? pokemons : pokemons.filter(p => p.types.includes(selectedType));
    console.log(filteredPokemon);
    // const newfilteredPokemon = selectedFuente === "All" ? filteredPokemon : filteredPokemon.filter(p => typeof p === "number");
    // const newfilteredPokemon = selectedType === "All" ? 
    // pokemons : 
    // pokemons.filter(p => 
    //     selectedType === "Number" ? 
    //         typeof p.id === "number" : 
    //         selectedType === "String" ? 
    //             typeof p.id === "string" : 
    //             false
    // );

  
   
    //Controlador de Enventos --> Tipos de pokemones
    const handleTypeChange = (e) => {
        setSelectedType(e.target.value);
      };
    //Controla el evento de Fuente
      const handleFuenteChange = (e) => {
        setSelectedFuente(e.target.value);
      };
    // cuando se carga la pagina, se llena el array vacio con pokemones
    useEffect(()=>{
        dispatch(getAllPokemos());
   
    },[]);
   
    

    // cuando se carga la pagina, se llena el array vacio con los tipos
    useEffect(()=>{
        dispatch(getAllTypes());
   
    },[]);
    
   


    return(
        <div className={styles.cont}>
        <TitleBar  type={"Principal"} text={"MANAGE POKEMONS¡¡"}/>
        {/* <a>{pokemones[0].name}</a> */}
      <br />
        <Search/>
        <br />
        <div className={styles.searchbar}>
        

            
        <div className={styles.filtra}>
            <div >Ordenar por :</div>
{/* //----------- Ordenamiento de Pokemones */}
            <select className={styles.opciones}>
                <option value="">A-Z</option>
                <option value="">Z-A</option>
                <option value="">Por vida</option>
                <option value="">Por ataque</option>
            </select>
            <div >Obtenido de:</div>
{/* //----------- Seleccion fuente (BD, API) de Pokemones */}
            <select className={styles.opciones} onChange={handleFuenteChange}>
                <option value="All" >All</option>
                <option value="string" >Bd</option>
                <option value="number" >Api</option>
             
            </select>
{/* //----------- Seleccion de tipos de Pokemones */}
            <div >Tipos de Pokemones:</div>
            <select value={selectedType}  onChange={handleTypeChange} className={styles.opciones}>
               {tipospokemones.map(tipo =>(
                    <option value={tipo} >{tipo}</option>
                ))
               }
                
             
            </select>
        </div>
        
        </div>


        <Cards pokemones={filteredPokemon}></Cards> // las cartas de pokemones
               

        <div className={styles.numeros}>
        {Array.from({ length: 36 }, (_, i) => i + 1).map((page) => (
                        <button className={styles.numero} key={page}  >
                            {page}
                        </button>
            ))}
        </div>
           

            <Footer/>
        </div>
    )
}
export default Ahome
