import { Router } from "react-router-dom"
import { useSelector, useDispatch } from "react-redux"; 
import { useEffect, useState } from "react";
import { getAllPokemos } from "../../Redux/actions";
import styles from "./Ahome.module.css";
import TitleBar from "../TitleBar/TitleBar";
import Footer from "../Footer/Footer";
import pika2 from "../../Assets/fondototal.jpg";
import Cards from "../Cards/Cards";

const Ahome= ()=>{
    const dispatch = useDispatch();
    const pokemones = useSelector(state => state.pokemones)

    
    useEffect(()=>{
        dispatch(getAllPokemos());
    },[]);
    
   
    


    return(
        <div className={styles.cont}>
        <TitleBar  type={"Principal"} text={"MANAGE POKEMONS¡¡"}/>
        {/* <a>{pokemones[0].name}</a> */}
        <div className={styles.searchbar}>
            <input className={styles.searinput} type="search" />
            <button
            className={styles.buttoninput}> Buscar
            </button>
            {Array.from({ length: 10 }, (_, i) => i + 1).map((page) => (
                        <button key={page}  >
                            {page}
                        </button>
            ))}
            

            
        <div className={styles.filtra}>
            <div >Ordenar por :</div>
            <select className={styles.opciones}>
                <option value="">A-Z</option>
                <option value="">Z-A</option>
                <option value="">Por vida</option>
                <option value="">Por ataque</option>
            </select>
        </div>

        </div>
        
        <Cards pokemones={pokemones}></Cards>
           

            <Footer/>
        </div>
    )
}
export default Ahome
